package com.ibm.math;

public class Maths {
	
	public Maths() {
		// TODO Auto-generated constructor stub
	}
	public int sum(int a, int b) {
		return a+b;
	}
	
	public int subs(int a, int b) {
		return a-b;
	}
	
	public int div(int a, int b) {
		return a/b;
	}
	public int square(int a ) {
		return a*a;
	}
}
